Tabulator.extendModule("edit","editors",{dateEditor:function(cell,onRendered,success,cancel,editorParams){"use strict";let editor=document.createElement("input");editor.setAttribute("type","date");editor.style.padding="3px";editor.style.width="100%";editor.style.boxSizing="border-box";editor.value=luxon.DateTime.fromISO(cell.getValue()).toFormat("yyyy-LL-dd");onRendered(function(){editor.focus();editor.style.css="100%"});function successFunc(){success(editor.value)}editor.addEventListener("change",successFunc);editor.addEventListener("blur",successFunc);return editor},datetimeEditor:function(cell,onRendered,success,cancel,editorParams){"use strict";let editor=document.createElement("input");editor.setAttribute("type","datetime");editor.style.padding="3px";editor.style.width="100%";editor.style.boxSizing="border-box";editor.value=luxon.DateTime.fromISO(cell.getValue()).toFormat("yyyy-LL-dd HH:mm:ss");onRendered(function(){editor.focus();editor.style.css="100%"});function successFunc(){success(editor.value)}editor.addEventListener("change",successFunc);editor.addEventListener("blur",successFunc);return editor}});